import com.chance.ssm.service.CapitalUserPoolService;

/**平台资金统计**/
@Controller
public class CapitalAction {
	
	@Autowired
	private CapitalPlatformService capitalPlatformService;
	
	@Autowired
	private CapitalNursePoolService capitalNursePoolService;
	
	@Autowired
	private CapitalUserPoolService capitalUserPoolService;
	
	@RequestMapping(value="capitalStatistics")
	public String capitalStatistics(Model model,Integer pageNow,HttpServletRequest request)throws Exception{
		BigDecimal SumMoneyToday = BigDecimal.ZERO;
		BigDecimal sumMoneyMonth = BigDecimal.ZERO;
		BigDecimal sumMoneyYear = BigDecimal.ZERO;
		
		SumMoneyToday = capitalPlatformService.SelCapitalSumMoney();
		sumMoneyMonth = capitalPlatformService.SelCapitalSumMoneyMonth();
		sumMoneyYear = capitalPlatformService.SelCapitalSumMoneyYear();
		
		if(SumMoneyToday == null){
			SumMoneyToday = BigDecimal.ZERO;
		}

		if(sumMoneyMonth == null){
			sumMoneyMonth =BigDecimal.ZERO;
		}

		if(sumMoneyYear == null){
			sumMoneyYear = BigDecimal.ZERO;
		}
		
		model.addAttribute("SumMoneyToday", SumMoneyToday);
		model.addAttribute("sumMoneyMonth", sumMoneyMonth);
		model.addAttribute("sumMoneyYear", sumMoneyYear);
		return "capital-platform";
	}
	//用户资金明细
	@RequestMapping(value="capitaluserinfo")
	public String capitaluserinfo(Model model,Integer pageNow,HttpServletRequest request)throws Exception{
		CapitalUserPoolExample example = new CapitalUserPoolExample();
		example.setOrderByClause("capital_user_pool_createtime DESC");
		List<CapitalUserPool> list = capitalUserPoolService.selectByExample(example);
		model.addAttribute("list", list);
		return "capital-user-info";
	}
	//护士资金明细
	@RequestMapping(value="capitalnurseinfo")
	public String capitalnurseinfo(Model model,Integer pageNow,HttpServletRequest request)throws Exception{
		CapitalNursePoolExample example = new CapitalNursePoolExample();
		example.setOrderByClause("capital_nurse_pool_createtime DESC");
		List<CapitalNursePool> list =  capitalNursePoolService.selectByExample(example);
		model.addAttribute("list", list);
		return "capital-nurse-info";
	}
	//平台资金明细
	@RequestMapping(value="capitalplatinfo")
	public String capitalplatinfo(Model model,Integer pageNow,HttpServletRequest request)throws Exception{
		CapitalPlatformExample example = new CapitalPlatformExample();
		example.setOrderByClause("capital_platform_createtime DESC");
		List<CapitalPlatform> list =  capitalPlatformService.selectByExample(example);
		model.addAttribute("list", list);
		return "capital-plat-info";
	}
}
