mapper.select:
select 

from nurse
where nurse_id = #{nurseId,jdbcType=CHAR}


  
  
   SELECT * FROM nurse 
   where  
   nurse_position_y > #{minY,jdbcType=DOUBLE}
  AND nurse_position_y < #{maxY,jdbcType=DOUBLE}  
  AND nurse_position_x >#{minX,jdbcType=DOUBLE}  
  AND nurse_position_x < #{maxX,jdbcType=DOUBLE}  
  and nurse_district = 0
  
  and
       nurse_work_unit = #{ nurseWorkUnit,jdbcType=VARCHAR}
  
  
  and
       nurse_office = #{ nurseOffice,jdbcType=VARCHAR}
  
  ORDER BY ACOS(SIN((#{locationX,jdbcType=DOUBLE} * 3.1415) / 180 ) * SIN((nurse_position_x * 3.1415) / 180 )   
  +COS((#{locationX,jdbcType=DOUBLE} * 3.1415) / 180 ) * COS((nurse_position_x * 3.1415) / 180 )   
  *COS((#{locationY,jdbcType=DOUBLE} * 3.1415) / 180 - (nurse_position_y * 3.1415) / 180 ) )   
  * 6380 DESC
  LIMIT 0,100

SELECT * FROM nurse   
WHERE nurse_position_x <> 0  
AND nurse_position_y > #{minY,jdbcType=DOUBLE}
AND nurse_position_y < #{maxY,jdbcType=DOUBLE}  
AND nurse_position_x >#{minX,jdbcType=DOUBLE}  
AND nurse_position_x < #{maxX,jdbcType=DOUBLE}  
AND nurse_work_unit <> #{userId,jdbcType=CHAR}
AND nurse_office <> #{majorId,jdbcType=CHAR}
ORDER BY ACOS(SIN((#{locationX,jdbcType=DOUBLE} * 3.1415) / 180 ) * SIN((user_position_x * 3.1415) / 180 )   
+COS((#{locationX,jdbcType=DOUBLE} * 3.1415) / 180 ) * COS((user_position_x * 3.1415) / 180 )   
*COS((#{locationY,jdbcType=DOUBLE} * 3.1415) / 180 - (user_position_y * 3.1415) / 180 ) )   
* 6380 ASC

  	SELECT u.user_nick, u.user_phone, u.user_header,(os.order_send_trafficmoney + os.order_send_costmoney) AS price,os.order_send_servicecontent, os.order_send_finish_order_time 
	FROM capital_nurse_pool, order_send os INNER JOIN order_receiver orr ON os.order_send_id = orr.order_ordersendid INNER JOIN USER u ON u.user_id = os.order_send_userid 
	WHERE orr.order_nurseid = #{nurseId,jdbcType=CHAR} AND os.order_send_id = capital_nurse_pool_ordersendid  order by os.order_send_finish_order_time DESC;

 SELECT n.nurse_id,nurse_header,nurse_nick, COUNT(f.follow_followid) AS followCounts
FROM nurse n LEFT JOIN follow f ON n.nurse_id=follow_befollowid 
AND follow_befollowid_iden =1  GROUP BY nurse_id ORDER BY followCounts DESC,nurse_createtime ASC;
